// src/MenuBar.js

import React, { useEffect } from "react";
import "./MenuBar.css"; // Import the CSS for styling

import { GoogleLogin } from "@react-oauth/google";
function navigate(url) {
  window.location.href = url; // goes to url
}

async function auth() {
  const response = await fetch("http://localhost:3001/request", {
    // backend request route
    method: "post",
  });
  const data = await response.json(); // gets url for auth request
  navigate(data.url); // navigates to url
}

const MenuBar = () => {
  const [user, setUser] = useState(null);
  useEffect(() => {
    // Check if user is logged in (you might check cookies, localStorage, etc.)
    getData();
  }, []);

  async function getData() {
    const response = await fetch("http://localhost:3001/user/data", {
      // backend request route
      method: "get",
      credentials: "include", // Important for sending cookies
    });

    if (!response.ok) {
      setUser(null);
      return;
    }

    const data = await response.json(); // gets url for auth request

    if (!data.success) {
      setUser(null);
      return;
    }
    setUser(data);
  }

  return (
    <div className="menu-bar">
      {/* placeholder title */}
      <h1 className="site-title">Game Sharing</h1>
      <nav>
        <ul className="menu">
          <li>
            <a href="#home">Home</a>
          </li>
          <li>
            <a href="#games">Games</a>
          </li>
          <li>
            <a href="#about">About</a>
          </li>
          <li>
            {user ? (
              <div>
                <h1>Welcome, {user.name}!</h1>
                <img src={user.picture} alt="Profile" />
                {/* Add more user-specific content here */}
                <button
                  onClick={() => {
                    localStorage.removeItem("access_token"); // Log out
                    setUser(null); // Clear user data
                  }}
                >
                  Logout
                </button>
              </div>
            ) : (
              <div>
                <h1>Welcome to Our App!</h1>
                <p>Please log in to access more features.</p>
                <button onClick={() => (window.location.href = "/login")}>
                  Login
                </button>
              </div>
            )}

            <button
              type="button"
              onClick={() => auth()}
              className="btn btn-outline-dark"
            >
              Login
            </button>
          </li>
        </ul>
      </nav>
    </div>
  );
};

export default MenuBar;
